import ReactSpeedometer from "react-d3-speedometer"

export default function Gauge({ min, max, lim, value}) {

    const segmentstop = (min,max,lim,value) => {
        const segment_stops = []
        const segment_colors = []
        const segment_labels = []
        if(lim > value){
            segment_stops = [min,min+1,value,lim,lim+1,max-1,max]
            segment_colors = ['#ff7e00','#ff7e00','#edeceb','black','#edeceb','#edeceb']
            segment_labels = [{ text:{min},position:'OUTSIDE',color:"black"},{color:"white"},{color:"white"},{color:"white"},{color:"white"},{ text:{max},position:'OUTSIDE',color:"black"}]
        }
        else if(lim == value){
            segment_stops = [min,min+1,lim,lim+1,max-1,max]
            segment_colors = ['green','green','black','#edeceb','#edeceb']
            segment_labels = [{ text:{min},position:'OUTSIDE',color:"black"},{color:"white"},{color:"white"},{color:"white"},{ text:{max},position:'OUTSIDE',color:"black"}]            
        }
        else{
            segment_stops = [min,min+1,lim,lim+1,value,max-1,max]
            segment_colors = ['red','red','black','red','#edeceb','#edeceb']            
            segment_labels = [{ text:{min},position:'OUTSIDE',color:"black"},{color:"white"},{color:"white"},{color:"white"},{ text:{max},position:'OUTSIDE',color:"black"}]            
        }
        return [segment_stops,segment_colors,segment_labels]
    }

return(  


    <ReactSpeedometer
        width={150}
        height={100}
        ringWidth={20} 
        minValue={min}
        maxValue={max}
        value={value}    
        segments={2}
        needleHeightRatio={0.2}
        needleColor="white"
        customSegmentStops={segmentstop(min,max,lim,value).segment_stops}
        customSegmentLabels={segmentstop(min,max,lim,value).segment_labels}
        segmentColors={segmentstop(min,max,lim,value).segment_colors}
        maxSegmentLabels={1}
        valueTextFontSize='14px'
        labelFontSize='10px'
    />
);
}
