import React from 'react';
import {
  IconButton,
  Grid,
  Text,
  useColorModeValue,
  Divider,
  Heading,
  GridItem,
  AspectRatio
} from '@chakra-ui/react';
import { Stat, StatNumber, StatHelpText } from '@chakra-ui/react';
import {
  FaPlaneDeparture,
  FaPlaneArrival,
  FaPlane,
  FaUsers,
  FaTruckLoading,
  FaSun,
  FaCloudSun,
  FaMoon,
  FaCloudMoon,
  FaBus,
  FaBuilding,
} from 'react-icons/fa';
import { ColorModeSwitcher } from '../ColorModeSwitcher';

import GaugeDemo from './gauge_main';

const highColor = statNum => {
  if (statNum > 1342) {
    return ['green'];
  } else if (statNum == 1342) {
    return ['red'];
  } else {
    return ['#ff7e00'];
  }
};

function StatCard({ bgColor, statNumber }) {
  return (
    <Stat
      align="center"
      bg={bgColor}
      py="2"
      border="2px"
      borderColor="white"
      borderRadius="5px"
    >
      <StatNumber fontSize={["sm", "md", "lg", "2xl"]}>1342</StatNumber>
      <StatHelpText fontSize={["sm", "md", "lg"]}>
        <IconButton
          size="sm"
          fontSize="sm"
          aria-label={`departure flight`}
          variant="ghost"
          color="current"
          icon={<FaPlaneDeparture />}
        />
        - 1132
        <br />
        <IconButton
          size="sm"
          fontSize="sm"
          aria-label={`departure flight`}
          variant="ghost"
          color="current"
          icon={<FaPlaneArrival />}
        />{' '}
        - 254
      </StatHelpText>
    </Stat>
  );
}
function StatCard_Arrival({ bgColor, statNumber }) {
  return (
    <Stat
      align="center"
      bg={bgColor}
      py="3"
      border="2px"
      borderColor="white"
      borderRadius="5px"
    >
      <StatNumber fontSize="xl">{statNumber}</StatNumber>
    </Stat>
  );
}

function StatCard_Depature({ bgColor, statNumber }) {
  return (
    <Stat
      align="center"
      bg={bgColor}
      py="3"
      border="2px"
      borderColor="white"
      borderRadius="5px"
    >
      <StatNumber fontSize="xl">{statNumber}</StatNumber>
    </Stat>
  );
}
function RowHeadings({ heading, icon, bgColor }) {
  return (
    <Text
      fontSize="md"
      align="center"
      py="3"
      bg={bgColor}
      whiteSpace="nowrap"
      wordBreak="revert"
    >
      <IconButton
        size="sm"
        fontSize="lg"
        aria-label={`departure flight`}
        variant="ghost"
        color="current"
        icon={icon}
      />
      {heading}
    </Text>
  );
}
function App() {
  // const highlightColor = useColorModeValue('blue.200', 'blue.800');
  const highlightColor = useColorModeValue('white', 'white');

  return (
    <Grid
      w="100%"
      h="100%"
      templateRows="repeat(2, 1fr)"
      templateColumns="repeat(2, 0fr)"
      gap={0}
      autoFlow='column'
    >
      <GridItem
        rowSpan={2}
        colSpan={1}
        border="2px"
        borderColor="gray.200"
        borderRadius="5px"
      >
        <Grid
          templateColumns="repeat(5, 1fr)"
          w={[
            "100%", // 0-30em
            "50%", // 30em-48em
            "25%", // 48em-62em
            "15%", // 62em+
          ]}
          rowGap={0}
          columnGap={0}
        >
          <RowHeadings
            heading="KLIA"
            icon={<FaBuilding />}
            bgColor="blue.300"
          />

          <RowHeadings heading="0000-0800" icon={<FaCloudSun />} />
          <RowHeadings
            heading="0800-1500"
            icon={<FaSun />}
            bgColor={highlightColor}
          />
          <RowHeadings heading="1500-2100" icon={<FaCloudMoon />} />
          <RowHeadings heading="2100-2400" icon={<FaMoon />} />

          <RowHeadings heading="PAX flight" icon={<FaPlane />} />
          <StatCard statNumber={1342} />
          <StatCard statNumber={1342} />
          <StatCard statNumber={1342} />
          <StatCard statNumber={1342} />
          <RowHeadings heading="PAX" icon={<FaUsers />} />
          <StatCard statNumber={1342} />
          <StatCard statNumber={1342} />
          <StatCard statNumber={1342} />
          <StatCard statNumber={1342} />
          <RowHeadings heading="Cargo flight" icon={<FaTruckLoading />} />
          <StatCard statNumber={1342} />
          <StatCard statNumber={1342} />
          <StatCard statNumber={1342} />
          <StatCard statNumber={1342} />
          <RowHeadings heading="P Mover" icon={<FaBus />} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.8} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.5} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.7} percentage={0.3} />
          <RowHeadings heading="P BB" icon={<FaBus />} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.8} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.7} percentage={0.3} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.5} />
          <RowHeadings heading="P Vroom" icon={<FaBus />} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.5} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.8} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.7} percentage={0.3} />
          <RowHeadings heading="KATS" icon={<FaBus />} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.8} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.7} percentage={0.3} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.5} />
        </Grid>

      </GridItem>

      <GridItem rowSpan={1} colSpan={1}>
        <Grid templateColumns="repeat(5, 1fr)" rowGap={0} 
        w={[
            "100%", // 0-30em
            "50%", // 30em-48em
            "25%", // 48em-62em
            "15%", // 62em+
          ]}
>
          <RowHeadings
            heading="Arrival"
            icon={<FaPlaneArrival />}
            bgColor={highlightColor}
            bgColor="blue.300"
          />

          <RowHeadings heading="0000-0800" icon={<FaCloudSun />} />
          <RowHeadings
            heading="0800-1500"
            icon={<FaSun />}
            bgColor={highlightColor}
          />
          <RowHeadings heading="1500-2100" icon={<FaCloudMoon />} />
          <RowHeadings heading="2100-2400" icon={<FaMoon />} />

          <RowHeadings heading="Immigration" icon={<FaBus />} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.5} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.8} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.7} percentage={0.3} />
          <RowHeadings heading="Luggage Claim" icon={<FaBus />} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.8} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.7} percentage={0.3} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.5} />
        </Grid>
      </GridItem>

      <GridItem rowSpan={1} colSpan={1}>
        <Grid templateColumns="repeat(5, 1fr)" w="100%" rowGap={0}>
          <RowHeadings
            heading="Departure"
            icon={<FaPlaneDeparture />}
            bgColor={highlightColor}
            bgColor="blue.300"
            borderRadius="55px"
          />
          <RowHeadings heading="0000-0800" icon={<FaCloudSun />} />
          <RowHeadings
            heading="0800-1500"
            icon={<FaSun />}
            bgColor={highlightColor}
          />
          <RowHeadings heading="1500-2100" icon={<FaCloudMoon />} />
          <RowHeadings heading="2100-2400" icon={<FaMoon />} />

          <RowHeadings heading="Check In" icon={<FaBus />} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.8} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.7} percentage={0.3} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.5} />
          <RowHeadings heading="Security" icon={<FaBus />} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.8} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.7} percentage={0.3} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.5} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.6} />
          <RowHeadings heading="Immigration" icon={<FaBus />} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.8} percentage={0.6} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.7} percentage={0.3} />
          <GaugeDemo bgColor={highlightColor} arcLim={0.5} percentage={0.5} />
        </Grid>
      </GridItem>
    </Grid>
  );
}

export default App;
