import ReactSpeedometer from "react-d3-speedometer"

export default function Gauge({ max, lim, value}) {

    const segmentstop = (min,max,lim,value) => {
        segment_stops = []
        segment_colors = []
        if(lim > value){
            segment_stops = [min,min+1,value,lim,lim+1,max-1,max]
            segment_colors = ['#ff7e00','#ff7e00','#edeceb','black','#edeceb','#edeceb']
        }
        else if(lim == value){
            segment_stops = [min,min+1,lim,lim+1,max-1,max]
            segment_colors = ['green','green','black','#edeceb','#edeceb']            
        }

    }

return(  


    <ReactSpeedometer
        width={150}
        height={100}
        ringWidth={20} 
        minValue={0}
        maxValue={303}
        value={120}    
        segments={2}
        needleHeightRatio={0.7}
        needleColor="white"
        customSegmentStops={[0,1,130,132,302,303]}
        customSegmentLabels={[{ text:"0",position:'OUTSIDE',color:"black"},{color:"white"},{color:"white"},{color:"white"},{ text:"303",position:'OUTSIDE',color:"black"}]}
        segmentColors={['#ff7e00','#ff7e00','black','#edeceb','#edeceb']}
        maxSegmentLabels={1}
        needleHeightRatio={0.2}
        valueTextFontSize='14px'
        labelFontSize='10px'
    />
);
}
